def nuc_wrap(dna, width):

    dna = re.sub("\n", "", dna)
    regex = re.compile("\w{1," + str(width) + "}") ## precompile our regular expression
    nuc_strings = regex.findall(dna)
    [print(x) for x in nuc_strings]

nuc_wrap(dna, 80)



 
